import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap';
import Avatar from 'react-avatar';
import './Login.css';
import avatar from './image.png';
import { Formik } from 'formik';
import * as yup from 'yup';
const Login = () => (
    <Formik
        initialValues={{
            UserName: '',
            Password: ''
        }}
        onSubmit={(value) => {
            console.log("The values are", value);
        }}
        validationSchema={yup.object(
            {
                UserName: yup.string()
                    .required('Required')
                    .min(4, 'Minimum 4 characters required'),
                Password: yup.string()
                    .required('Required')
                    .min(4, 'Minimum 4 characters required')
                    .max(8, 'Maximum 8 characters required')
            }
        )}
    >
        {({ handleChange, handleSubmit, errors, values }) => (
            <div className='contain'>
                <div className="card">
                    <h5 className="card-title">Login Form</h5>
                    <div className="card-body">
                        <div className="avatar">
                            <Avatar src={avatar} round={true} size="100" />
                        </div>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label>User Name:</label>
                                <input type="text" className="form-control" onChange={handleChange} name="UserName" value={values.UserName} />
                                {errors.UserName ? (errors.UserName) : null}
                            </div>
                            <div className="form-group">
                                <label>Password:</label>
                                <input type="text" className="form-control" onChange={handleChange} name="Password" value={values.Password} />
                                {errors.Password ? (errors.Password) : null}
                            </div>
                            <div className="button">
                                <Button type="submit" className="btn-lg btn-block">Submit</Button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        )
        }
    </Formik>
);
export default Login;
