import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap';
import Avatar from 'react-avatar';
import avatar from '../image.png';
function Buttons({history}){
    function handlesubmit1()
    {
        setTimeout(() => {
            alert("The Account is Deleted");
        },3000);
        history.pushState('./Login');
    }
    return(
    <div className='contain' style={{
        width:'100%',
        height: '100%',
        paddingTop:'150px',
        backgroundColor:'rgb(250, 112, 112)'
    }}>
    <div className="card" style={{
            width: '60%',
            height:'60%',
            marginLeft: '240px',
            backgroundColor:'grey'
    }}>
    <h5 className="card-title" style={{
            textAlign:'center',
            paddingTop:'80px'
    }}>Click!!!</h5>
    <div className="card-body" style={{
           position:'relative',
           bottom:'90px'
    }}>
    <div className="avatar" style={{
            position: 'relative',
            left: '390px',
            bottom: '90px'
    }}>
        <Avatar src={avatar} round={true} size="100"/>
        </div>
        <Button type="submit"className="btn-lg"style={{marginLeft:'100px'}} onClick={handlesubmit1()}>Delete Account!!</Button>
        <Button type="submit"className="btn-lg" id="button2" style={{marginLeft:'300px'}}>Change Password</Button>
    </div>
    </div>
    </div>
    );
}
export default Buttons;
