import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'react-bootstrap';
import Avatar from 'react-avatar';
import './Change.css';
import avatar from './image.png';
import { Formik } from 'formik';
const Change=()=>
(
    <Formik
    initialValues={{
        Password:'',
        ConfirmPassword:''
    }}
    onSubmit={(values)=>
    {
        console.log("The values are",values);
    }}
    >
        {(handleChange,handleSubmit,errors,values)=>
        (
    <div className='contain'>
    <div className="card">
    <h5 className="card-title">Change Password</h5>
    <div className="card-body">
     <form onSubmit={handleSubmit}>   
    <div className="avatar">
        <Avatar src={avatar} round={true} size="100"/>
    </div>
    <div className="form-group">
        <label>Enter New Password</label>
     <input type="text" 
     className="form-control" 
     name="Password"
     value={values.Password}
     onChange={handleChange}
     />
    {errors.Password ? <div className="text-danger" style={{height:'0px'}}>*{errors.Password}*</div>:null}
    </div>
    <div className="form-group">
        <label>ConfirmPassword</label>
        <input 
        type="password" 
        className="form-control" 
        name="ConfirmPassword"
        value={values.ConfirmPassword}
        onChange={handleChange}/>
       {errors.ConfirmPassword ? <div className="text-danger" style={{height:'0px'}}>*{errors.ConfirmPassword}*</div>:null}
    </div>
    <Button type="submit"className="btn-block" id="button">Change Password</Button>
    </form>    
    </div>
    </div>
    </div>
        )
        }
    </Formik>
)
export default Change;
